// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
error pleasecomeLater();
error InvalidMatterNumber();
contract klg{


      struct ContractData{
        address Address;
        string assetName;
        uint256 matter_number;
        string file_hash;
        uint effective_dates;
        uint escrowAmount;
        uint noOfRelease;
        uint totalRelease;
        uint noOfDay;
        bool agreed;
      }


    //struct
    struct Partner{
        address Address;
        uint64 id;
    }

    struct Client{
        address Address;
        uint64 id;
    }

    //mappings
    mapping (address=> mapping (address=> ContractData[])) public contractData;
    mapping (address=> Partner) public partner;
    mapping (address=> Client) public client;

    uint matterNumber;
    address public ContractOwner;
    IERC20 public token;

    constructor(IERC20 _token)  
    {
        token =  IERC20(_token);
        ContractOwner=msg.sender;    
    }




    //modifiers
      modifier onlyOwner {
      require(msg.sender ==  ContractOwner,"only owner can do");
      _;
   }
    modifier onlyClient {
      require(msg.sender==client[msg.sender].Address,"only client can access");
      _;
   }
    modifier onlyPartner {
      require(msg.sender==partner[msg.sender].Address,"only partner can access");
      _;
   }

    //functions
    function CreatePartner(
        address _Address
        ) 
        public  onlyOwner
        {   
            require(_Address!= partner[_Address].Address,"partner already created");
            partner[_Address].Address=_Address;
            partner[_Address].id= partner[_Address].id+1;
        }


    function CreateClient( 
        address _Address
        ) 
        public  onlyOwner
        {
            require(_Address!= client[_Address].Address,"client Already Created");
            client[_Address].Address=_Address;
            client[_Address].id=client[_Address].id+1;
        }

     function CreateContract(
        address _address,
        string memory _assetName,
        string memory file_hash,
        uint _escrowAmount,
        uint _noOfReleases,
        uint _noOfDay
        )
        public  
    {
        require(msg.sender==partner[msg.sender].Address,"you are not partner");
        require(_address==client[_address].Address,"the Client in not existed");
        matterNumber++;
        uint _totalReleases=0;
        uint  effective_dates=0;

        contractData[msg.sender][_address].push(ContractData(_address,_assetName,matterNumber,file_hash,effective_dates,_escrowAmount,_noOfReleases,_totalReleases,_noOfDay,false));
        console.log(token.totalSupply());
       token.transferFrom(msg.sender,address(this),_escrowAmount);


    }

    function Claim(
        address _partnerAddress,
        address _clientAddress,
        uint256 _matter_number
        )
        public  onlyClient
    {
        require(_clientAddress==client[_clientAddress].Address,"the Client in not existed");
        require(_partnerAddress==partner[_partnerAddress].Address,"partner doesnot existed");
        uint l=contractData[_partnerAddress][_clientAddress].length;
        // ContractData[] memory record=new ContractData[](l);
        for(uint i=0; i < l; ++i)
        {
            if(contractData[_partnerAddress][_clientAddress][i].matter_number==_matter_number)
               {
                   require( contractData[_partnerAddress][_clientAddress][i].agreed==true,"client not agreed yet");
                 uint release=contractData[_partnerAddress][_clientAddress][i].totalRelease+1;

                 uint numberDays=contractData[_partnerAddress][_clientAddress][i].noOfDay* 1 minutes;
                 if(block.timestamp>=
                  contractData[_partnerAddress][_clientAddress][i].effective_dates + (numberDays * release))
                  {
                     uint GivingAmount=contractData[_partnerAddress][_clientAddress][i].escrowAmount;
                    uint amount=GivingAmount/contractData[_partnerAddress][_clientAddress][i].noOfRelease;
                    contractData[_partnerAddress][_clientAddress][i].totalRelease+1;
                    token.transfer(_clientAddress, amount);
                  }

                }

        }


    }


    function AggrementDeal(
        address _partnerAddress,
        address _clientAddress,
        uint256 _matter_number
        )
         public onlyClient
          returns(
            ContractData[] memory CONTRACTDATA
        )
          {
        require(_clientAddress==client[_clientAddress].Address,"the Client in not existed");
        require(_partnerAddress==partner[_partnerAddress].Address,"partner doesnot existed");
        // require(contractData[_partnerAddress][_clientAddress][0].Address==_clientAddress,"you are not allowed");
          uint l=contractData[_partnerAddress][_clientAddress].length;
        ContractData[] memory record=new ContractData[](l);
        for(uint i=0; i < l; ++i){
            require(contractData[_partnerAddress][_clientAddress][i].Address==_clientAddress,"you are not allowed");
            // console.log("log",contractData[_partnerAddress][_clientAddress][i].matter_number);
            console.log("log1",_matter_number);
            if(contractData[_partnerAddress][_clientAddress][i].matter_number==_matter_number){
                 console.log("log",contractData[_partnerAddress][_clientAddress][i].matter_number);
                contractData[_partnerAddress][_clientAddress][i].agreed=true;
                contractData[_partnerAddress][_clientAddress][i].effective_dates=block.timestamp;
            record[i] = contractData[_partnerAddress][_clientAddress][i];
            }

        }
          CONTRACTDATA = record;



    } 


    function ContractDetails( address _partnerAddress,address _clientAddress) 
        public
        view
        returns(
            ContractData[] memory CONTRACTDATA
        )
    {
        uint l=contractData[_partnerAddress][_clientAddress].length;
        ContractData[] memory record=new ContractData[](l);
        for(uint i=0; i < l; ++i){
      record[i] = contractData[_partnerAddress][_clientAddress][i];
    }
    CONTRACTDATA = record;

    }





}

